import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getApi, Delete } from "../counter/counterSlice";
import { useNavigate } from "react-router-dom";

export const Api = () => {
  const Navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, data, del } = useSelector((state) => state);

  useEffect(() => {
    dispatch(getApi());
  }, [del]);

  useEffect(() => {
    dispatch(getApi());
  }, []);

  function deletePost(id) {
    dispatch(Delete(id));
    window.location.reload();
  }

  return (
    <div>
      <h1>Api</h1>

      <table
        border="2px"
        align="center"
        cellPadding={"12px"}
        cellSpacing={"0px"}
      >
        <thead>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Image</th>
            <th>createdAt</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <>loading....</>
          ) : (
            data?.map((item, index) => {
              return (
                <tr key={index}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>

                  <td>
                    <img src={item.avatar} alt="image" />
                  </td>

                  <td>{item.createdAt}</td>
                  <td>
                    <button onClick={() => deletePost(item.id)}>Delete</button>
                    {"  "}
                    <button>Edit</button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
};
