import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getApi } from "../counter/counterSlice";

export const Api = () => {
  const dispatch = useDispatch();
  const { loading, data } = useSelector((state) => state);
  // console.log(dispatch(getApi()));
  // console.log(data);
  // dispatch(getApi())

  return (
    <div>
      <h1>Api</h1>
      <button onClick={() => dispatch(getApi())}>Click me</button>
      <table>
        <tr>
          <th>Id</th>
          <th>Title</th>
        </tr>
        {loading ? (
          <div>loading....</div>
        ) : (
          data.map((item) => {
            return (
              <>
                <h2>{item.id}</h2>
                <h2>{item.title}</h2>
              </>
            );
          })
        )}
      </table>
    </div>
  );
};
