import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import PropTypes from "prop-types";
import Backdrop from "@mui/material/Backdrop";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
// web.cjs is required for IE11 support
import { useSpring, animated } from "react-spring/web.cjs";

const Fade = React.forwardRef(function Fade(props, ref) {
  const { in: open, children, onEnter, onExited, ...other } = props;
  const style = useSpring({
    from: { opacity: 0 },
    to: { opacity: open ? 1 : 0 },
    onStart: () => {
      if (open && onEnter) {
        onEnter();
      }
    },
    onRest: () => {
      if (!open && onExited) {
        onExited();
      }
    },
  });

  return (
    <animated.div ref={ref} style={style} {...other}>
      {children}
    </animated.div>
  );
});

Fade.propTypes = {
  children: PropTypes.element,
  in: PropTypes.bool.isRequired,
  onEnter: PropTypes.func,
  onExited: PropTypes.func,
};

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};


// const initialState = {
//   value: 0,
//   data: [],
// };
export const getApi = createAsyncThunk("posts/getPost", async () => {
  return fetch("https://629dae743dda090f3c07dd7f.mockapi.io/test")
    .then((item) => item.json())
    .then((result) => result)
    .catch((error) => error);
});

export const Delete = createAsyncThunk("posts/deletePost", async (id) => {
  return fetch(`https://629dae743dda090f3c07dd7f.mockapi.io/test/${id}`, {
    method: "DELETE",
  })
    .then((item) => item.json())
    .then((result) => result)
    .catch((error) => console.log(error));
});

export const Update = createAsyncThunk("posts/deletePost", async (id) => {
  return fetch(`https://629dae743dda090f3c07dd7f.mockapi.io/test/${id}`, {
    method: "POST",
  })
    // .then((item) => item.json())
    // .then((result) => result)
    // .catch((error) => console.log(error));
});

export const counterSlice = createSlice({
  name: "counter",
  initialState: {
    value: 0,
    data: [],
    error: null,
    del: "",
  },
  loading: false,
  reducers: {
    increment: (state) => {
      // Redux Toolkit allows us to write "mutating" logic in reducers. It
      // doesn't actually mutate the state because it uses the Immer library,
      // which detects changes to a "draft state" and produces a brand new
      // immutable state based off those changes
      state.value += 1;
    },
    decrement: (state) => {
      state.value -= 1;
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    },
  },
  extraReducers: {
    [getApi.pending]: (state, action) => {
      state.loading = true;
    },
    [getApi.fulfilled]: (state, action) => {
      state.loading = false;
      state.data = action.payload;
    },
    [getApi.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    [Delete.pending]: (state, action) => {
      state.loading = true;
    },
    [Delete.fulfilled]: (state, action) => {
      state.loading = false;
      state.delete = action.payload;
    },
    [Delete.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

// Action creators are generated for each case reducer function
export const { increment, decrement, incrementByAmount } = counterSlice.actions;

export default counterSlice.reducer;
